<div><h3 class="font-bold mb-4">Riwayat Pekerjaan</h3><button wire:click="addWorkExperience" type="button" class="mb-3 px-4 py-2 bg-green-600 text-white rounded text-sm">+ Tambah</button><div class="space-y-3">@forelse($workExperiences as $index => $work)<div class="bg-gray-50 p-4 rounded"><div class="flex justify-between mb-2"><h4 class="font-semibold">Pekerjaan {{$index+1}}</h4><button wire:click="removeWorkExperience({{$index}})" type="button" class="text-red-600 text-sm">Hapus</button></div><div class="grid grid-cols-3 gap-3"><div><input type="text" wire:model="workExperiences.{{$index}}.company" placeholder="Nama Perusahaan *" class="w-full px-3 py-2 border rounded text-sm"></div><div><input type="text" wire:model="workExperiences.{{$index}}.position" placeholder="Jabatan *" class="w-full px-3 py-2 border rounded text-sm"></div><div><input type="number" wire:model="workExperiences.{{$index}}.salary" placeholder="Gaji" class="w-full px-3 py-2 border rounded text-sm"></div><div><input type="text" wire:model="workExperiences.{{$index}}.period" placeholder="Periode Kerja *" class="w-full px-3 py-2 border rounded text-sm"></div><div class="col-span-2"><input type="text" wire:model="workExperiences.{{$index}}.reason" placeholder="Alasan Pindah" class="w-full px-3 py-2 border rounded text-sm"></div></div></div>@empty<p class="text-gray-500 text-sm">Belum ada data</p>@endforelse</div><div class="mt-6 flex justify-end"><button wire:click="saveWorkHistory" type="button" class="px-6 py-3 bg-blue-600 text-white rounded-lg">Simpan</button></div></div>
